mapper.select:
select count(*) from 
  (select u.u_name,
  		t.t_name,
  		a.a_name

  	 from users u,
  	 	teacher t,
  	 	admin a 

  where u.u_name=#{name}
    or t.t_name=#{name}
    or a.a_name=#{name})

select u_email 
	from users
where u_idx=#{u_idx}

select u_idx
	 from users
where u_tel=#{u_tel}

select u_pwd, u_state
from users 
where u_email=#{e_mail}

	select u_name, u_idx
  	from users
  	where users.u_email=#{e_mail}

	select t_idx 
  	from teacher
  	where teacher.u_idx =#{u_idx}

select u_email , u_state
from users
where u_tel=#{u_tel} 
	and u_gender=#{u_gender}

select u_state
from users 
where u_email=#{e_mail}

mapper.update:
update users
	set u_pwd=#{u_pwd}

where u_email=#{u_email}

update users 

	set u_pwd=#{u_pwd} ,
		u_name=#{u_name},
		u_img=#{u_img},
		u_tel=#{u_tel}

where u_idx=#{u_idx}

update users 
set u_state='탈퇴' 
where u_idx=#{u_idx}

update 
	users
set 
	U_POINT = #{newUPoint}
where 
	U_IDX = #{uIdx}

update 
	users
set 
	U_POINT = U_POINT + #{point}
where 
	U_IDX = #{uIdx}

update 
	users
set 
	U_POINT = U_POINT - #{point}
where 
	U_IDX = #{uIdx}
